package javaBasic0513;

public class forEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
	
		for(int a=1; a<8;a++) {
			for(int b=4; b>0;b--) {
				if(b>a) {
					System.out.print(" ");
				}else{
					System.out.print("*");
				}
				}
			System.out.println();
        }	

	
	
	}

}
	
	
	
	
	

