package javaBasic0513;

public class forEx6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	for(int a=1;a<5;a++) {
		for(int b=1;b<=4-a;b++) {
			System.out.print(" ");
		}
		for(int c=1;c<=(2*a)-1;c++) {
			System.out.print("*");
		}
		System.out.println();	
	}
	for(int a=3;a>0;a--) {
		for(int b=1;b<=4-a;b++) {
			System.out.print(" ");
		}
		for(int c=1;c<=(2*a)-1;c++) {
			System.out.print("*");
		}
		System.out.println();	
	}

	
	
	}

}
	
	
	
	
	

